import axios from 'axios';
export default {
  data() {
    return {
      event: {
        name: '',
        description: '',
        date: ''
      },
      successMessage: '',
      errorMessage: ''
    };
  },
  methods: {
    createEvent() {
      axios.post('http://localhost:3000/api/events', this.event).then(() => {
        // Removed 'response' as it is unused
        this.successMessage = 'Event created successfully!';
        this.errorMessage = ''; // Clear any previous errors
        this.event = {
          name: '',
          description: '',
          date: ''
        }; // Reset form
      }).catch(() => {
        // Removed 'error' as it is unused
        this.errorMessage = 'Failed to create event!';
        this.successMessage = ''; // Clear success message
      });
    }
  }
};
