import { createApp } from 'vue';
import App from './App.vue';
import { createRouter, createWebHistory } from 'vue-router';
import EventList from './components/EventList.vue';
import EditEvent from './components/EditEvent.vue';
import Login from './components/Login.vue';   // Login component
import Register from './components/Register.vue'; // Register component
import EventDetails from './components/EventDetails.vue';  // Import EventDetails component
import Toast, { POSITION } from "vue-toastification";
import "vue-toastification/dist/index.css";
// Define the routes
const routes = [
    { path: '/', redirect: { name: 'login' } }, // Redirect root path to login page
    { path: '/login', component: Login, name: 'login' },  // Login route
    { path: '/register', component: Register, name: 'register' }, // Registration route
    { path: '/events', component: EventList, name: 'events', meta: { requiresAuth: true } },  // EventList route, protected by authentication
    { path: '/events/:id/edit', component: EditEvent, name: 'edit-event', props: true, meta: { requiresAuth: true } }, // Edit event, protected
    { path: '/events/:id', component: EventDetails, name: 'event-details', props: true, meta: { requiresAuth: true } }  // Event details route

];

// Create the router
const router = createRouter({
  history: createWebHistory(),
  routes
});
const app = createApp(App);

// Use the Toastification plugin
app.use(Toast, {
    // options can be added here
    timeout: 3000, // Set timeout for 3 seconds
    position: "top-right"
  });

// Add a navigation guard to protect routes
router.beforeEach((to, from, next) => {
    // Check if the route requires authentication
    if (to.matched.some(record => record.meta.requiresAuth)) {
      const token = localStorage.getItem('token');

      if (!token) {
        // If not authenticated, redirect to login page
        return next({ name: 'login' });
      }
    }

    // If the route does not require auth or the user is authenticated, proceed
    next();
  });

app.use(router);
app.mount('#app');
